mapper.select:
select *
from users
where user_id = #{userId} and user_password = #{userPassword}

mapper.update:
update users


		user_tel = #{userTel}


		, zipcode = #{zipcode}


		, addr = #{addr}


		, addr_detail = #{addrDetail}


where user_id = #{userId}

mapper.select:
select *
from users


		user_id = #{userId}


		user_name = #{userName}


		user_tel = #{userTel}


		addr = #{addr}


		user_quit = #{userQuit}


order by user_insert_date
